mapper.select:
SELECT 
FROM cm_customer_info


    AND CUSTOMER_ID = #{customerId,jdbcType=DECIMAL}


    AND CUSTOMER_NAME LIKE CONCAT('%', CONCAT(UPPER(#{customerName,jdbcType=VARCHAR}), '%'))


    AND BUSINESS_TYPE LIKE CONCAT('%', CONCAT(UPPER(#{businessType,jdbcType=VARCHAR}), '%'))


    AND BUSINESS_PRICE = #{businessPrice,jdbcType=DECIMAL}


    AND MANAGER_NAME LIKE CONCAT('%', CONCAT(UPPER(#{managerName,jdbcType=VARCHAR}), '%')))


    AND MANAGER_PHONE = #{managerPhone,jdbcType=VARCHAR}


    AND RECEIVER LIKE CONCAT('%', CONCAT(UPPER(#{receiver,jdbcType=VARCHAR}), '%'))


    AND RECEIVING_CONTACT = #{receivingContact,jdbcType=VARCHAR}


    AND CONTACT_PHONE = #{contactPhone,jdbcType=VARCHAR}


    AND RECEIPT_LOCATION = #{receiptLocation,jdbcType=VARCHAR}


    AND BILL_BOARD_LOCATION = #{billBoardLocation,jdbcType=VARCHAR}


    AND LOADING_LOCATION = #{loadingLocation,jdbcType=VARCHAR}


    AND DISCHARGE_LOCATION = #{dischargeLocation,jdbcType=VARCHAR}


    AND COUNTER_LOCATION = #{counterLocation,jdbcType=VARCHAR}


    AND REMARK = #{remark,jdbcType=VARCHAR}
