mapper.select:
SELECT DISTINCT oo.SALES_ORDER_ID, oo.ORDER_NUMBER, cci.CUSTOMER_NAME, iii.TANK_NO, cct.RECEIVER, oo.CREATE_DATE, oo.SHIPPING_DATE, oo.ORDER_STATUS FROM om_sales_order oo LEFT JOIN cm_customer_info cci ON oo.CUSTOMER_ID = cci.CUSTOMER_ID LEFT JOIN cm_customer_type cct ON oo.CUSTOMER_TYPE_ID = cct.TYPE_ID LEFT JOIN im_item_info iii ON iii.SALES_ORDER_ID = oo.SALES_ORDER_ID LEFT JOIN om_sales_line_car oslc ON oslc.SALES_ORDER_ID = oo.SALES_ORDER_ID
AND oo.ORDER_NUMBER LIKE CONCAT('%', CONCAT(UPPER(#{orderNumber,jdbcType=VARCHAR}), '%'))
AND oo.ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}
AND oo.CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
AND oo.SHIPPING_DATE >= #{shippingDateFrom,jdbcType=TIMESTAMP}
AND oo.SHIPPING_DATE <= #{shippingDateTo,jdbcType=TIMESTAMP}
AND oo.CUSTOMER_ID = #{customerId,jdbcType=DECIMAL}
AND cci.CUSTOMER_NAME LIKE CONCAT('%', CONCAT(UPPER(#{customerName,jdbcType=VARCHAR}), '%'))
AND cct.RECEIVER LIKE CONCAT('%', CONCAT(UPPER(#{receiver,jdbcType=VARCHAR}), '%'))
AND iii.TRANSACTION_NO LIKE CONCAT('%', CONCAT(UPPER(#{transactionNo,jdbcType=VARCHAR}), '%'))
AND iii.TANK_NO LIKE CONCAT('%', CONCAT(UPPER(#{tankNo,jdbcType=VARCHAR}), '%'))
AND iii.WAYBILL_NO LIKE CONCAT('%', CONCAT(UPPER(#{wayBillNo,jdbcType=VARCHAR}), '%'))
AND oslc.CAR_FRONT_NO LIKE CONCAT('%', CONCAT(UPPER(#{carFrontNo,jdbcType=VARCHAR}), '%'))

SELECT 
  FROM om_sales_order


          AND SALES_ORDER_ID = #{salesOrderId,jdbcType=DECIMAL}


          AND ORDER_NUMBER LIKE CONCAT('%', CONCAT(UPPER(#{orderNumber,jdbcType=VARCHAR}), '%'))


          AND ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}


          AND CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


          AND SHIPPING_DATE = #{shippingDate,jdbcType=TIMESTAMP}


          AND CUSTOMER_ID = #{customerId,jdbcType=DECIMAL}


          AND CUSTOMER_TYPE_ID = #{customerTypeId,jdbcType=DECIMAL}
